import React from "react";
import "./SideBar.css";

function SideBar({ repos, loadingRepos, onRepoSelect }) {
    return (
        <div className="sidebar">
            <h3>Repositories</h3>
            <ul>
                {repos.map((repo, index) => (
                    <li key={index} onClick={() => onRepoSelect(repo)}>
                        {repo}
                    </li>
                ))}
                {loadingRepos.map((repo, index) => (
                    <li key={`loading-${index}`} className="loading-repo">
                        {repo} <span className="loading-spinner"></span>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default SideBar;
