import React, { useState } from "react";
import "./SideBar.css";

function SideBar({ repos, onRepoSelect, onCloneRepo }) {
    const [loading, setLoading] = useState(false);

    const handleCloneRepo = async () => {
        setLoading(true);
        try {
            await onCloneRepo();
        } catch (error) {
            console.error("Error cloning repo:", error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="sidebar">
            <h3>Repositories</h3>
            <ul>
                {repos.map((repo, index) => (
                    <li key={index} onClick={() => onRepoSelect(repo)}>
                        {repo}
                    </li>
                ))}
            </ul>
            {loading ? (
                <div className="loading">Cloning Repo...</div>
            ) : (
                <button className="clone-repo-btn" onClick={handleCloneRepo}>
                    Clone Repo
                </button>
            )}
        </div>
    );
}

export default SideBar;
