import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, useNavigate } from "react-router-dom";
import HomePage from "./HomePage";
import ChatPage from "./ChatPage";

function App() {
    const [repos, setRepos] = useState([]); // Store repositories
    const [isLoading, setIsLoading] = useState(false); // Loading state
    const [isAuthenticated, setIsAuthenticated] = useState(false); // Authentication state

    const navigate = useNavigate();

    useEffect(() => {
        // Check if the user is authenticated
        fetch("/check-auth")
            .then((res) => res.json())
            .then((data) => {
                setIsAuthenticated(data.isAuthenticated);
            })
            .catch((error) => {
                console.error("Error checking authentication:", error);
            });
    }, []);

    const handleCloneRepos = async () => {
        setIsLoading(true); // Start loading
        try {
            const response = await fetch("/clone-repos", { method: "POST" });
            if (response.ok) {
                const reposData = await response.json();
                setRepos(reposData.repos);
                setIsLoading(false); // Stop loading
                navigate("/chat"); // Redirect to chat page
            } else {
                console.error("Failed to clone repositories.");
                setIsLoading(false);
            }
        } catch (error) {
            console.error("Error cloning repositories:", error);
            setIsLoading(false);
        }
    };

    return (
        <Router>
            {isLoading ? (
                <div className="loading">
                    <h2>Loading...</h2>
                    <div className="spinner"></div>
                </div>
            ) : (
                <Routes>
                    <Route
                        path="/"
                        element={
                            <HomePage
                                isAuthenticated={isAuthenticated}
                                handleCloneRepos={handleCloneRepos}
                            />
                        }
                    />
                    <Route path="/chat" element={<ChatPage repos={repos} />} />
                </Routes>
            )}
        </Router>
    );
}

export default App;
