import React from 'react';

function Sidebar({ repos, onSelectRepo }) {
    return (
        <div className="sidebar">
            <h2>Your Repositories</h2>
            <ul>
                {repos.map((repo, index) => (
                    <li key={index} onClick={() => onSelectRepo(repo)}>
                        {repo}
                    </li>
                ))}
            </ul>
            <button>Add More Repos</button>
        </div>
    );
}

export default Sidebar;
