import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function HomePage() {
    const [repos, setRepos] = useState([]);
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    const handleGitHubLogin = () => {
        window.location.href = "/oauth/github";
    };

    const handleCloneRepos = async () => {
        setIsLoading(true);
        try {
            const response = await fetch("/clone-repos", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ repos }),
            });

            if (response.ok) {
                setIsLoading(false);
                navigate("/chat");
            } else {
                console.error("Failed to clone repositories.");
                setIsLoading(false);
            }
        } catch (error) {
            console.error("Error cloning repositories:", error);
            setIsLoading(false);
        }
    };

    return (
        <div className="homepage">
            <h1>Welcome to the RAG Codebase App</h1>
            {!isAuthenticated ? (
                <button onClick={handleGitHubLogin}>Connect to GitHub</button>
            ) : (
                <>
                    <button onClick={handleCloneRepos} disabled={isLoading}>
                        {isLoading ? "Cloning..." : "Clone Repositories"}
                    </button>
                </>
            )}
        </div>
    );
}

export default HomePage;
