import React, { useState } from "react";
import "./Sidebar.css";

function Sidebar({ selectedRepo, setSelectedRepo }) {
    const [repos, setRepos] = useState([]);

    const handleCloneRepo = async () => {
        const response = await fetch("/repos");
        const repoList = await response.json();

        const repoUrl = prompt("Enter the repository URL to clone:");
        if (!repoUrl) return;

        await fetch("/embed", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ repo_url: repoUrl }),
        });

        setRepos([...repos, repoUrl.split("/").pop()]);
    };

    return (
        <div className="sidebar">
            <h2>Repositories</h2>
            <ul>
                {repos.map((repo, index) => (
                    <li
                        key={index}
                        className={selectedRepo === repo ? "selected" : ""}
                        onClick={() => setSelectedRepo(repo)}
                    >
                        {repo}
                    </li>
                ))}
            </ul>
            <button className="clone-btn" onClick={handleCloneRepo}>
                Clone Repo
            </button>
        </div>
    );
}

export default Sidebar;
