function SideBar({ repos, onRepoSelect }) {
    return (
        <div className="sidebar">
            <h3>Repositories</h3>
            <ul>
                {repos.map((repo, index) => (
                    <li
                        key={index}
                        className="repo-item"
                        onClick={() => onRepoSelect(repo)}
                        style={{ cursor: "pointer" }} // Add hover effect for better UX
                    >
                        {repo}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default SideBar;
