import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import ChatWindow from './components/ChatWindow';

function App() {
    const [repos, setRepos] = useState([]);
    const [selectedRepo, setSelectedRepo] = useState(null);

    const connectGithub = () => {
        // Redirect to the backend for GitHub OAuth
        window.location.href = '/oauth/github';
    };

    const handleRepoSelect = (repo) => {
        setSelectedRepo(repo);
    };

    return (
        <div className="app">
            {repos.length === 0 ? (
                <div className="welcome">
                    <h1>Welcome to RAG Codebase App</h1>
                    <button onClick={connectGithub}>Connect with GitHub</button>
                </div>
            ) : (
                <div className="app-container">
                    <Sidebar repos={repos} onSelectRepo={handleRepoSelect} />
                    <ChatWindow repo={selectedRepo} />
                </div>
            )}
        </div>
    );
}

export default App;
